import { BRow, BCol, BAvatar, BFormInput, BInputGroupPrepend, BInputGroup } from 'bootstrap-vue';
import Ripple from 'vue-ripple-directive';
import { addressEnCode, addressDecode } from '@/libs/utils';
export default {
  name: 'AppFooter',
  components: {
    BCol,
    BRow,
    BAvatar,
    BFormInput,
    BInputGroupPrepend,
    BInputGroup
  },
  directives: {
    Ripple
  },

  data() {
    return {
      selectedAddress: 'cosmos1ehjgrsfgz38faq0pe6nruma3wmszm29k0efv9w'
    };
  },

  computed: {
    chains() {
      const {
        data
      } = addressDecode('cosmos1ehjgrsfgz38faq0pe6nruma3wmszm29k0efv9w');
      const config = Object.values(JSON.parse(localStorage.getItem('chains'))).map(x => ({
        addr: addressEnCode(x.addr_prefix, data),
        icon: x.logo
      }));
      return config;
    }

  },
  methods: {
    transfer(addr) {
      this.selectedAddress = addr;
      this.$bvModal.show('transfer-window');
    }

  }
};
