import { ValidationProvider } from 'vee-validate';
import { BFormRadioGroup, BFormRadio, BFormGroup } from 'bootstrap-vue';
import { getLocalAccounts } from '@/libs/utils';
export default {
  name: 'WalletInput',
  components: {
    BFormRadioGroup: BFormRadioGroup,
    BFormRadio: BFormRadio,
    BFormGroup: BFormGroup,
    ValidationProvider: ValidationProvider
  },
  props: {
    value: {
      type: String,
      default: 'keplr'
    }
  },
  data: function data() {
    return {
      content: this.value
    };
  },
  methods: {
    handleInput: function handleInput() {
      this.$emit('input', this.content);
      var accounts = getLocalAccounts();
      var wallet = accounts[this.$store.state.chains.defaultWallet];

      if (wallet) {
        wallet.device = this.content;
        localStorage.setItem('accounts', JSON.stringify(accounts)); // update signer device
      }
    }
  }
};
