      })
      return result
    }
    if (this.config.chain_name === 'echelon') {
      const result = await this.get('/echelon/inflation/v1/params').then(data => data.params)
      await this.get('/echelon/inflation/v1/period').then(data => {
        Object.entries(data).forEach(x => {
