          const v = x[1]
          result[k] = v
        })
      })
      await this.get('/echelon/inflation/v1/total_supply').then(data => {
        Object.entries(data).forEach(x => {
          const k = x[0]
          const v = x[1]
          result[k] = v
        })
      })
      return result
    }
    if (this.isModuleLoaded('minting')) {
      return this.get('/minting/parameters').then(data => commonProcess(data))
    }
    return null
